def solution(stdin: str) -> str:
    key = "P"
    plaintext = stdin.strip()

    def decrypt(d, key):
        if len(key) % 2!= 0:
            return None

        for i in range(len(key) // 2):
            key[i] = key[len(key) - 1 - i]

        for i in range(len(plaintext) // 2):
            plaintext[i] = plaintext[len(plaintext) - 1 - i]

        for i in range(len(key) // 2):
            if key[i] == d:
                return plaintext[i] + plaintext[i + len(key) // 2]
            elif key[i] == plaintext[i]:
                return None

        for i in range(len(key) // 2):
            if key[i] == key[len(key) - 1 - i]:
                return None

        for i in range(len(key) // 2):
            if key[i] == key[i]:
                return None

        return None

    encrypted = ""
    for i in range(len(plaintext) // 2):
        encrypted += decrypt(key[i], plaintext[i])

    return encrypted.upper()

