        students_in_room = []
        for _ in range(room):
            student = int(stdin.strip())
            students_in_room.append(student)
        students_in_room.sort()
        students_in_room.reverse()
        for student in students_in_room:
            for exam in pile:
                if exam == student:
                    pile.remove(exam)

    if len(exams_in_pile) == n_students:
        return "impossible"

    for i in range(n):
        for j in range(i + 1, n):
            if rooms[i] < rooms[j]:
                return str(rooms[i]) + " " + str(rooms[j])

