def solution(stdin: str) -> str:
    a, b = map(int, stdin.split())
    operations = [0] * (a+1)
    operations[0] = 0
    operations[1] = 1
    operations[2] = 2
    for i in range(3, a+1):
        if i % 2 == 0:
            operations[i] = 1
        else:
            operations[i] = 2
    min_operations = a
    for i in range(a, -1, -1):
        if operations[i]!= min_operations:
            min_operations = operations[i]
            break
    return str(min_operations)
