    reachable_numbers = set()

    for i in range(1, n + 1):
        if i in reachable_numbers:
            continue
        trailing_zeroes = 0
        num = i
        while num % 10 == 0:
            num //= 10
            trailing_zeroes += 1
        if trailing_zeroes > 0:
            num -= 10 ** trailing_zeroes
            if num in reachable_numbers:
                continue
        reachable_numbers.add(num)

    return str(len(reachable_numbers))

