        if index == n:
            if sum(result) == n:
                return True
            else:
                return False

        for i in range(start, n):
            if i > 0 and i % 2 == 0:
                continue
            if i > 0 and i % 3 == 0:
                continue
            if i > 0 and i % 4 == 0:
                continue
            if i > 0 and i % 5 == 0:
                continue
            if result[index] == i:
                continue
            result.append(i)
            if backtrack(i + 1, index + 1, result):
                return True
            result.pop()

        return False

    if backtrack(1, 0, []):
        return''.join(map(str, result))
    else:
        return '-1'

