    # Helper function to calculate the minimum total coins required
    def min_coins(x, i, coins):
        if i == 0:
            return coins[0]
        if x[i] - 2 in coins:
            coins.remove(x[i] - 2)
        if x[i] + 2 in coins:
            coins.remove(x[i] + 2)
        if x[i] - 1 in coins:
            coins.remove(x[i] - 1)
        if x[i] + 1 in coins:
            coins.remove(x[i] + 1)
        return min(min_coins(x, i-1, coins), min_coins(x, i-2, coins))

    # Calculate the minimum total coins required
    return str(min_coins(x, n-1, [x[i]]*n))

