def solution(stdin: str) -> str:
    n, k = map(int, stdin.strip().split())
    s = list(map(int, stdin.strip().split()))

    def merge_sort(arr):
        if len(arr) <= 1:
            return arr

        mid = len(arr) // 2
        left = merge_sort(arr[:mid])
        right = merge_sort(arr[mid:])

        return merge(left, right)

    def merge(left, right):
        result = []
        i = j = 0

        while i < len(left) and j < len(right):
            if left[i] < right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1

        result.extend(left[i:])
        result.extend(right[j:])

        return result

    def cut_out(s, t, i):
        j = 0
        for j in range(i, len(s)):
            if s[j]!= t[i]:
                break

        s.pop(j)
        return s

    t = merge_sort(s)
    t = t[:k]

    max_copies = 0
    max_indices = []

    for i in range(len(t) - k + 1):
        cut_indices = []
        for j in range(k):
            cut_indices.append(j)

        while len(cut_indices) < k:
            cut_index = cut_out(t, s, cut_indices[-1])
            if len(cut_index) > max_copies:
                max_copies = len(cut_index)
                max_indices = cut_indices

        cut_indices = [cut_index[-1] for _ in range(len(cut_indices) - 1)]
        cut_indices.append(i)
        t = cut_indices

        for j in range(len(t) - k + 1):
            cut_indices = []
            for k in range(k):
                cut_indices.append(j)

            while len(cut_indices) < k:
                cut_index = cut_out(t, s, cut_indices[-1])
                if len(cut_index) > max_copies:
                    max_copies = len(cut_index)
                    max_indices = cut_indices

        cut_indices = [cut_index[-1] for _ in range(len(cut_indices) - 1)]
        cut_indices.append(i)
        t = cut_indices

    return''.join(map(str, max_indices))

