def solution(stdin: str) -> str:
    n, k = map(int, stdin.strip().split())
    roads = []

    for _ in range(n - 1):
        x, y = map(int, stdin.strip().split())
        roads.append((x, y))

    def dfs(start, seen, path, path_len, unvisited):
        if path_len == n:
            if len(unvisited) == k:
                return unvisited.pop()
            else:
                return -1

        for neighbor, weight in roads[start]:
            if neighbor not in seen:
                if neighbor not in unvisited:
                    path.append(neighbor)
                    unvisited.append(neighbor)
                    result = dfs(neighbor, seen + [neighbor], path, path_len + 1, unvisited)
                    if result == -1:
                        return -1
                    path.pop()
                    unvisited.pop()
                    return result
                elif neighbor in unvisited:
                    return neighbor

    unvisited = []
    for _ in range(n):
        if _ not in unvisited:
            result = dfs(_, set(), [], 0, unvisited)
            if result == -1:
                return "No solution"
            unvisited.append(_)

    return " ".join(map(str, unvisited))

