    def binary_search(start: int, end: int, target: int) -> int:
        if start == end:
            return A[start]
        mid = (start + end) // 2
        if A[mid] == target:
            return mid
        elif A[mid] > target:
            return binary_search(start, mid - 1, target)
        else:
            return binary_search(mid + 1, end, target)

    left, right = 0, n - 1
    while left <= right:
        mid = (left + right) // 2
        if A[mid] == target:
            return str(mid)
        elif A[mid] < target:
            left = mid + 1
        else:
            right = mid - 1

    return str(binary_search(0, n - 1, target))

