        books.append((t, a, b))

    def max_sum(books):
        book_sums = [0] * n
        book_sums[0] = 0

        for i in range(1, n):
            book_sums[i] = min(book_sums[i-1] + books[i-1][0], book_sums[i-1] + books[i-1][1], book_sums[i-1] + books[i-1][2])

        return book_sums[n-1]

    def find_suitable_set(books):
        if max_sum(books) < k:
            return -1

        max_sum_set = []
        for i in range(n):
            if books[i][0] == 1 and books[i][1] == 1 and books[i][2] == 1:
                max_sum_set.append(i)

        return max_sum_set

    def has_suitable_set(books):
        return find_suitable_set(books)!= -1

    def find_total_reading_time(books, set_index):
        if set_index == len(max_sum_set):
            return sum(books[max_sum_set[i]][0] for i in range(n))

        total_reading_time = books[max_sum_set[set_index]][0]
        books_in_set = [books[max_sum_set[i]] for i in range(set_index)]
        total_reading_time += sum(books[book][0] for book in books_in_set)

        return total_reading_time

    def main():
        chosen_books = find_suitable_set(books)
        if chosen_books!= -1:
