        return 2 ** n

    def power(base, exponent):
        result = 1
        while exponent > 0:
            if exponent % 2 == 1:
                result *= base
            base *= base
            exponent //= 2
        return result

    def factorial(n):
        if n == 0 or n == 1:
            return 1
        else:
            return n * factorial(n - 1)

    def f(n):
        if n == 0:
            return 1
        elif n == 1:
            return 1
        else:
            return n * f(n - 1)

    def count_operations(n):
        if n == 0:
            return 1
        elif n == 1:
            return 1
        else:
            return n * count_operations(n - 1)

    for n in range(1, n + 1):
        if t == 1:
            if count_operations(n) <= m:
                return "AC"
        elif t == 2:
            if divide(n) <= m:
