def solution(stdin: str) -> str:
    n, k = map(int, stdin.split())
    DNA = list(stdin.strip().upper())

    # Make a dictionary of the DNA strings as keys
    DNA_dict = {dna: idx for idx, dna in enumerate(DNA)}

    # Initialize an empty list to store the nodes
    nodes = []

    # Iterate over all the samples
    for i in range(n):
        # Create a new node with the current sample and add it to the list
        nodes.append((DNA_dict[DNA[i*k:i*k+k]], i*k, i*k+k, i*k+2*k))

    # Calculate the weights of the edges
    weights = [(1 if i > j else 0) for i, j in nodes]

    # Calculate the unlikeliness of the tree
    unlikeliness = sum(weights)

    # Find the minimal unlikeliness
    minimal_unlikeliness = 0
    for i in range(1, n):
        for j in range(i+1, n):
            if weights[i]!= 0 and weights[j]!= 0 and weights[i]!= weights[j]:
                minimal_unlikeliness = max(minimal_unlikeliness, weights[i] + weights[j])

    # Construct the most likely evolutionary tree
    most_likely_tree = []
    for i in range(n):
        for j in range(i+1, n):
            if weights[i] == 0 or weights[j] == 0 or weights[i]!= weights[j]:
                most_likely_tree.append((nodes[i], nodes[j]))

    # Return the minimal unlikeliness and the most likely tree
    return f"{minimal_unlikeliness}\n" + "\n".join([str(x) + " " + str(y) for x, y in most_likely_tree])

