    dimensions = len(message)
    rows = dimensions // 4
    cols = dimensions % 4

    # Find the most suitable matrix
    max_rows = 0
    max_matrix = None
    for i in range(rows):
        matrix = [None] * cols
        for j in range(cols):
            matrix[j] = i + j
        if len(matrix) > max_rows:
            max_rows = len(matrix)
            max_matrix = matrix

    # Write the message into the matrix
    for i in range(rows):
        for j in range(cols):
            matrix[i][j] = message[i*cols + j]

    # Read the matrix in column-major order
    for i in range(rows):
        for j in range(cols):
            message += matrix[i][j]

    return message

