def solution(stdin: str) -> str:
    N, C = map(int, stdin.split())
    message = list(map(int, stdin.split()))

    # Sort the message using a modified merge sort
    def merge_sort(arr):
        if len(arr) <= 1:
            return arr

        mid = len(arr) // 2
        left = arr[:mid]
        right = arr[mid:]

        left = merge_sort(left)
        right = merge_sort(right)

        return merge(left, right)

    def merge(left, right):
        result = []
        i = j = 0

        while i < len(left) and j < len(right):
            if left[i] < right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1

        result += left[i:]
        result += right[j:]

        return result

    def frequency_analysis(arr):
        freq = {}

        for num in arr:
            if num in freq:
                freq[num] += 1
            else:
                freq[num] = 1

        return freq

    sorted_message = merge_sort(message)
    sorted_message = [sorted_message[i] for i in range(N)]

    sorted_freq = frequency_analysis(sorted_message)

    sorted_result = []

    for num in sorted_freq:
        if sorted_freq[num] > N:
            sorted_result.append(num)
        else:
            for i in range(N - sorted_freq[num]):
                sorted_result.append(sorted_message[i])

    return''.join(map(str, sorted_result))

