def solution(stdin: str) -> str:
    n = int(stdin.strip())
    if n < 1:
        return "Error: n must be a positive integer."

    def h(n):
        if n == 1:
            return 1
        elif n % 2 == 0:
            return n, h(n // 2)
        else:
            return n, h(3 * n + 1)

    h_seq = h(n)
    h_sum = 0
    for h_num in h_seq:
        h_sum += h_num

    return str(h_sum)

