def solution(stdin: str) -> str:
    N = int(stdin.readline())
    minutes = []

    for _ in range(N):
        M, S = map(int, stdin.readline().split())
        minutes.append((M, S))

    average_seconds = sum(map(lambda x: x[1], minutes)) / N

    if average_seconds >= 60:
        return "measurement error"

    return str(average_seconds) + "0" * (6 - len(str(average_seconds)))

def solution(stdin: str) -> str:
    encrypted_message, key = stdin.strip().split()

    # Initialize the cipher and key
    cipher = {
        'A': 'B', 'B': 'C', 'C': 'D', 'D': 'E', 'E': 'F', 'F': 'G', 'G': 'H', 'H': 'I', 'I': 'J', 'J': 'K', 'K': 'L', 'L': 'M', 'M': 'N', 'N': 'O', 'O': 'P', 'P': 'Q', 'Q': 'R', 'R': 'S', 'S': 'T', 'T': 'U', 'U': 'V', 'V': 'W', 'W': 'X', 'X': 'Y', 'Y': 'Z', 'Z': 'A'
    }

    decrypted_message = ''

    # Iterate over each even-indexed character in the encrypted message
    for i, char in enumerate(encrypted_message):
        if i % 2 == 0:
            # If the character is even, decrypt it using the cipher
            decrypted_char = cipher[char]
        else:
            # If the character is odd, encrypt it using the cipher
            decrypted_char = cipher[char]

        # Add the decrypted character to the decrypted message
        decrypted_message += decrypted_char

    return decrypted_message

def solution(stdin: str) -> str:
    moves = stdin.split()
    left_cup_index = -1
    middle_cup_index = -1
    right_cup_index = -1

    for i, move in enumerate(moves):
        if move == 'A':
            left_cup_index = i
        elif move == 'B':
            middle_cup_index = i
        elif move == 'C':
            right_cup_index = i

    if left_cup_index == -1 or middle_cup_index == -1 or right_cup_index == -1:
        return "Error: No cups found."

    return str(left_cup_index + middle_cup_index + right_cup_index)

