def solution(stdin: str) -> str:
    from functools import reduce

    def to_binary(num: int) -> str:
        result = ""
        while num > 0:
            result = str(num & 1) + result
            num >>= 1
        return result

    def to_signed_binary(num: int) -> str:
        result = ""
        if num < 0:
            result = "1" + result
        while num > 0:
            result = str(num & 1) + result
            num >>= 1
        return result

    def reverse_bits(num: int) -> int:
        return reduce(lambda x, y: x * 2 + y, [int(bit) for bit in bin(num)[2:].zfill(8)])

    def add(x: int, y: int) -> int:
        if x > 255 or y > 255:
            raise ValueError("Value out of range")
        return x + y

    def subtract(x: int, y: int) -> int:
        if x > 255 or y > 255 or x + y > 255:
            raise ValueError("Value out of range")
        return x - y

    def main(input_data: str) -> str:
        expressions = input_data.split()
        stack = []

        for expression in expressions:
            op = expression[0]
            operand = int(expression[1:])

            if op == "+":
                stack.append(add(stack.pop(), operand))
            elif op == "-":
                stack.append(subtract(stack.pop(), operand))
            elif op == "<":
                if not stack or operand > stack[-1]:
                    stack.append(1)
                else:
                    stack.append(0)
            elif op == ">":
                if not stack or operand < stack[-1]:
                    stack.append(1)
                else:
                    stack.append(0)
            elif op == ">=":
                if not stack or operand >= stack[-1]:
                    stack.append(1)
                else:
                    stack.append(0)
            elif op == "<=":
                if not stack or operand <= stack[-1]:
                    stack.append(1)
                else:
                    stack.append(0)
            elif op == ",":
                if stack:
                    print(to_signed_binary(stack.pop()), end=" ")
            elif op == ".":
                if stack:
                    print(to_signed_binary(stack.pop()), end=" ")
            elif op == "[":
                if stack and stack[-1] == 0:
                    stack.pop()
            elif op == "]":
                if stack and stack[-1] == 1:
                    stack.append(int(input("Enter integer: ")))
            elif op == "X":
                stack[-1] = 1 - stack[-1]
            elif op == "J":
                if stack:
                    stack[-1] += int(input("Enter integer: "))
            elif op == "D":
                if stack:
                    stack[-1] -= int(input("Enter integer: "))
            elif op == "R":
                if stack:
                    stack.append(stack[-1] + reverse_bits(int(input("Enter integer: "))))
            elif op == "K":
                if stack:
                    stack.append(stack[-1] * int(input("Enter integer: ")))
            elif op == "I":
                if stack:
                    print(to_signed_binary(stack.pop()))
            else:
                raise ValueError("Invalid operation")

        return stack[0] if stack else "0"

    return main(stdin)

