    # Determine if there are multiple ways to cut the rectangle
    max_area = 0
    max_ways = 0

    for i in range(W):
        for j in range(H):
            if (i, j) == (x, y):
                continue
            if (i, j) == (x, y - 1) or (i, j) == (x, y + 1):
                continue
            if (j, i) == (x - 1, y) or (j, i) == (x + 1, y):
                continue
