    n, m = map(int, stdin.split())
    edges = [list(map(int, stdin.split())) for _ in range(m)]

    def dfs(u, visited, visited_parent, visited_child):
        visited[u] = True
        visited_parent[u] = True
        visited_child[u] = True

        for v in edges[u]:
            if not visited[v]:
                dfs(v, visited, visited_parent, visited_child)

        return visited

    visited = [False] * n
    visited_parent = [False] * n
    visited_child = [False] * n

    for u in range(n):
        if not visited[u]:
            dfs(u, visited, visited_parent, visited_child)

    connected_component = 0
    for u in range(n):
        if visited_parent[u]:
            connected_component += 1

    return connected_component

