def solution(stdin: str) -> str:
    K = int(stdin.strip())

    if K <= 0:
        return "Invalid input. Please enter a positive integer."

    def combinations(n):
        return [i for i in range(1, n+1)]

    def choose_pair(numbers):
        if len(numbers) < 2:
            return []

        pairs = []
        for i in range(len(numbers)):
            for j in range(i+1, len(numbers)):
                pairs.append(numbers[i], numbers[j])

        return pairs

    def count_ways(numbers):
        pairs = choose_pair(numbers)
        pairs.sort()
        combinations = combinations(len(numbers))

        count = 0
        for combination in combinations:
            pairs_to_choose = [pair for pair in pairs if pair[0] in combination and pair[1] in combination]
            count += len(pairs_to_choose)

        return count

    result = count_ways(range(1, K+1))
    return str(result)

