        visited = set()
        visited.add(u)
        diameter = 1
        while queue:
            u = queue.pop(0)
            for v in graph[u]:
                if v not in visited:
                    visited.add(v)
                    queue.append(v)
                    if len(visited) > diameter:
                        diameter = len(visited)
        return diameter

