    visited = set()

    def dfs(x, y, path):
        if (x, y) in visited:
            return

        visited.add((x, y))
        path += f"U"
        if x == n - 1 and y == 0:
            return

        if x > 0:
            dfs(x - 1, y, path)

        if y > 0:
            dfs(x, y - 1, path)

        if x < n - 1:
            dfs(x + 1, y, path)

        if y < n - 1:
            dfs(x, y + 1, path)

        visited.remove((x, y))
        path += f"R"

    dfs(0, 0, "")

    if len(shortest_path) == 0:
        return "NO"

    return shortest_path

