    for i in range(N):
        S[i] = i + 1

    for i in range(M):
        C[i] = i + 1

    S.append(N)
    C.append(M)

    def dfs(i, p):
        if len(S) == N and len(C) == M:
            return True

        if S[i] == N or C[i] == M:
            return False

        if i < len(S) - 1:
            if S[i] > S[i + 1]:
                p.append(S[i + 1])
                S[i + 1] = i
                return dfs(i + 1, p)
            elif S[i] < S[i + 1]:
                p.append(S[i])
                S[i] = i
                return dfs(i, p)
            else:
                p.append(S[i])
                return dfs(i, p)
        else:
            p.append(C[i])
            return dfs(i, p)

    Scc_groups = []
    for i in range(N):
        p = []
        if dfs(i, p):
            Scc_groups.append(p)

    return str(max(Scc_groups))

