    for i in range(L):
        char = program[i]

        if char == '(':
            parentheses_stack.append(i)
            open_delimiters.append(char)
        elif char == ')':
            if len(parentheses_stack) == 0:
                error_count += 1
                continue
            closing_delimiter = program[parentheses_stack.pop()]
            if closing_delimiter!= '(':
                error_count += 1
            close_delimiters.append(closing_delimiter)
        elif char == '[':
            parentheses_stack.append(i)
            open_delimiters.append(char)
        elif char == ']':
            if len(parentheses_stack) == 0:
                error_count += 1
                continue
            closing_delimiter = program[parentheses_stack.pop()]
            if closing_delimiter!= '[':
                error_count += 1
            close_delimiters.append(closing_delimiter)
        elif char == '{':
            parentheses_stack.append(i)
            open_delimiters.append(char)
        elif char == '}':
            if len(parentheses_stack) == 0:
                error_count += 1
                continue
            closing_delimiter = program[parentheses_stack.pop()]
            if closing_delimiter!= '{':
                error_count += 1
            close_delimiters.append(closing_delimiter)

    if len(parentheses_stack)!= 0:
        error_count += 1

    if error_count > 0:
        return'syntax error'

    if error_count == 0:
        for i in range(L):
            if program[i] == ')' and program[i + 1] == '(':
                return str(i + 1) +'' + close_delimiters[i]
        return 'ok so far'
    else:
        return'syntax error'

