    T = list(map(int, stdin.split()))
    X = list(map(int, stdin.split()))

    # Find the longest milestone and its position
    max_milestone = -1
    for i in range(M):
        if T[i] > max_milestone:
            max_milestone = T[i]
            max_milestone_index = i

    # Find the shortest milestone and its position
    min_milestone = float('inf')
    min_milestone_index = -1
    for i in range(M):
        if T[i] < min_milestone:
            min_milestone = T[i]
            min_milestone_index = i

    # Calculate the distance between the two milestones
    distance = 0
    if max_milestone_index == min_milestone_index:
        distance = 0
    else:
        distance = (max_milestone_index - min_milestone_index) * X[max_milestone_index]

    # Calculate the possible speeds
    speeds = []
    current_speed = 0
    for i in range(M):
        if i == max_milestone_index:
            current_speed += distance
        elif i == min_milestone_index:
            current_speed += distance * (i + 1) / M
        else:
            current_speed += distance * (i - 1) / M
        speeds.append(current_speed)

    # Calculate the possible distances between the first and second milestones
    distances = []
    current_distance = 0
    for i in range(1, N):
        current_distance += X[i]
        if i == max_milestone_index:
            distances.append(current_distance)
            current_distance = 0
        elif i == min_milestone_index:
            distances.append(current_distance * (i + 1) / M)
            current_distance = 0
        else:
            distances.append(current_distance * (i - 1) / M)

    # Sort the distances and speeds in increasing order
    speeds = sorted(speeds)
    distances = sorted(distances)

    return '\n'.join(map(str, [len(speeds),''.join(map(str, speeds))] + distances))

