    for i in range(len(words)):
        for j in range(i + 1, len(words)):
            # Check if words are mutually anagrams
            if words[i]!= words[j] and words[i]!= words[len(words) - 1 - j] and words[j]!= words[len(words) - 1 - i]:
                # Check if current word is a multigram
                is_multigram = True
                for k in range(i + 1, len(words)):
                    if words[k]!= words[i] and words[k]!= words[j] and words[k]!= words[len(words) - 1 - j] and words[k]!= words[len(words) - 1 - i]:
                        is_multigram = False
                        break
                # If current word is not a multigram, break
                if not is_multigram:
                    break
                # If current word is a multigram, return shortest root
                return words[i]
