    def dfs(row, col):
        if row < 0 or col < 0 or row >= R or col >= C or grid[row][col]!= '#':
            return False
        grid[row][col] =''
        for dr, dc in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
            nr, nc = row + dr, col + dc
            if nr >= 0 and nr < R and nc >= 0 and nc < C and grid[nr][nc] == '#':
                return False
        for dr, dc in [(1, 1), (1, -1), (-1, 1), (-1, -1)]:
            nr, nc = row + dr, col + dc
            if nr >= 0 and nr < R and nc >= 0 and nc < C and grid[nr][nc] == '#':
                return False
        return True

    for row in range(R):
        for col in range(C):
            if dfs(row, col):
                return ''.join(grid[row]).lower()

    raise ValueError("No solution found")

