    N, M = map(int, stdin.split())

    def dfs(x, y, visited, path, min_zones, current_zones):
        visited[x][y] = True
        path.append((x, y))

        if x == 0 or y == 0 or x == N - 1 or y == M - 1:
            min_zones = max(min_zones, current_zones + 1)
            return

        if not visited[x - 1][y] and stdin[x - 1][y] == 'S' and current_zones + 1 == min_zones:
            dfs(x - 1, y, visited, path, min_zones, current_zones + 1)

        if not visited[x + 1][y] and stdin[x + 1][y] == 'S' and current_zones + 1 == min_zones:
            dfs(x + 1, y, visited, path, min_zones, current_zones + 1)

        if not visited[x][y - 1] and stdin[x][y - 1] == 'S' and current_zones + 1 == min_zones:
            dfs(x, y - 1, visited, path, min_zones, current_zones + 1)

        if not visited[x][y + 1] and stdin[x][y + 1] == 'S' and current_zones + 1 == min_zones:
            dfs(x, y + 1, visited, path, min_zones, current_zones + 1)

        path.pop()
        visited[x][y] = False

    def dfs_helper(x, y, path, min_zones, current_zones):
        visited = [[False] * M for _ in range(N)]
        visited[x][y] = True
        path.append((x, y))

        if x == 0 or y == 0 or x == N - 1 or y == M - 1:
            min_zones = max(min_zones, current_zones + 1)
            return

        if not visited[x - 1][y] and stdin[x - 1][y] == 'S' and current_zones + 1 == min_zones:
            dfs_helper(x - 1, y, path, min_zones, current_zones + 1)

        if not visited[x + 1][y] and stdin[x + 1][y] == 'S' and current_zones + 1 == min_zones:
            dfs_helper(x + 1, y, path, min_zones, current_zones + 1)

        if not visited[x][y - 1] and stdin[x][y - 1] == 'S' and current_zones + 1 == min_zones:
            dfs_helper(x, y - 1, path, min_zones, current_zones + 1)

        if not visited[x][y + 1] and stdin[x][y + 1] == 'S' and current_zones + 1 == min_zones:
            dfs_helper(x, y + 1, path, min_zones, current_zones + 1)

        path.pop()
        visited[x][y] = False

    visited = [[False] * M for _ in range(N)]
    visited[0][0] = True
    path = [(0, 0)]
    min_zones = 0
    current_zones = 1

    while visited[path[-1][0]][path[-1][1]]:
        current_zones += 1
        visited = [[False] * M for _ in range(N)]
        path = [(0, 0)]
        min_zones = 0
        current_zones = 1
        dfs(path[-1][0], path[-1][1], visited, path, min_zones, current_zones)

    if current_zones < min_zones:
        return "NOT POSSIBLE"

    return str(min_zones)

