    group_score = 1
    for i in range(1, n):
        if scores[i] > scores[i-1]:
            group_score += scores[i] ** (4/5) ** (i-1)

    current_group_score = round(group_score, 6)

    new_group_scores = []
    for score in scores:
        if score > current_group_score:
            new_group_scores.append(current_group_score + (score - current_group_score) ** (4/5) ** (n-1))
        else:
            new_group_scores.append(current_group_score)

    new_group_scores.sort()
