        return c[i] > 0

    def find_slot(is_cold):
        slot = 0
        while is_cold(slot, c):
            slot += 1
        return slot

    def refill(is_cold):
        slot = find_slot(is_cold)
        if is_cold(slot, c):
            return False
        c[slot] += m
        for i in range(slot, s):
            if is_cold(i, c):
                c[i] -= m
                break
        return True

    def max_probability(is_cold):
        if refill(is_cold):
            return sum(is_cold(i, c) for i in range(s))
        else:
            return "impossible"

    optimal_refills = []
    for is_cold in [True, False]:
        refill_scheme = max_probability(is_cold)
        if refill_scheme!= "impossible":
            optimal_refills.append(refill_scheme)

    return " ".join(map(str, optimal_refills))

