    def manhattan_distance(x1, y1, x2, y2):
        return abs(x1 - x2) + abs(y1 - y2)

    def min_distance(matrix, start, end, path):
        if start == end:
            path.append(start)
            return 0

        if start >= n * n:
            return -1

        if matrix[start // n][start % n] == matrix[end // n][end % n]:
            return min_distance(matrix, start + 1, end, path)

        path.append(start)
        return min_distance(matrix, start + 1, end, path) + manhattan_distance(start // n, start % n, end // n, end % n)

    path = []
    path = min_distance(matrix, 1, k, path)
    return str(path[-1]) if path else "-1"

