        return abs(t2 - t1)

    def rearrange_pools(t: List[int]) -> List[int]:
        i = 0
        while i < len(t) - 1:
            if min_temp_difference(t[i], t[i + 1]) <= min_temp_difference(t[i], t[i + 2]):
                t[i], t[i + 2] = t[i + 2], t[i + 1]
                t[i + 1], t[i] = t[i], t[i + 1]
            i += 1
        return t

    t_sorted = sorted(t for i, t in enumerate(t_sorted) if i < n)
    sorted_pools = rearrange_pools(t_sorted)

    if len(sorted_pools)!= n:
        return "impossible"

    for pool in sorted_pools:
        if pool not in pools:
            return "impossible"

    return " ".join(map(str, sorted_pools))

