def solution(stdin: str) -> str:
    key = []
    plaintext = stdin.strip().split()

    # Generate the key
    for letter in plaintext:
        if letter == 'q':
            break
        key.append(letter)

    # Fill in the rest of the table
    key = key * 5

    # Apply the encryption rules
    for i in range(len(plaintext)):
        # Replace identical letters
        if plaintext[i] == plaintext[i+1]:
            key[i] = 'X'
        else:
            # Replace letters on the same row
            if key[i] == key[i+1]:
                key[i] = chr(ord(key[i]) + 26)
            else:
                # Replace letters on the same column
                if key[i+1] == key[i+2]:
                    key[i] = chr(ord(key[i]) - 26)
                else:
                    # Replace non-matching letters
                    key[i] = key[i+1]

    # Convert the key back to a string
    key = ''.join(key)

    # Encrypt the plaintext
    encrypted = ''
    for letter in plaintext:
        encrypted += key[ord(letter.lower()) - ord('a')]

    return encrypted.upper()

