    rooms = [[] for _ in range(n)]
    for i in range(n):
        rooms[i] = list(map(int, stdin.strip().split()))
    visited = set()
    safe_order = []

    def dfs(room_idx):
        if room_idx in visited:
            return False

        visited.add(room_idx)
        safe_order.append(room_idx)

        if len(rooms[room_idx]) < n:
            return True

        for i in range(room_idx + 1, n):
            if i not in visited and i in rooms[room_idx]:
                rooms[i].remove(i)
                if not dfs(i):
                    return False
                rooms[i].append(room_idx)
                return True
        return False

    for i in range(n):
        if not dfs(i):
            return "impossible"

    return " ".join(str(x) for x in safe_order)

