    for i in range(P - 1, -1, -1):
        if w_list[i] not in parts_dict:
            break
        parts_dict[i] = w_list[i]

    # Find the day of the last replacement
    for i in range(N - 1, P, -1):
        if w_list[i] not in parts_dict:
            break
        parts_dict[i] = w_list[i]

    # Find the last day of the season
    season_end_day = i + 1

    return str(season_end_day)

