    # Check if the square is valid
    def is_valid_square(tiles, i, j):
        # Check if the diagonal is valid
        if i == j:
            return True
        # Check if the diagonal is the same as the previous row
        prev_row_sum = sum(tiles[i - 1][j])
        if prev_row_sum == tiles[i + 1][j]:
            return True
        # Check if the diagonal is the same as the previous column
        prev_col_sum = sum(tiles[i][j - 1])
        if prev_col_sum == tiles[i][j + 1]:
            return True
        return False

    def is_valid_tile(tiles, i, j):
        # Check if the tile is in the square
        if i < 0 or i >= len(tiles) or j < 0 or j >= len(tiles[0]):
            return False
        # Check if the tile is covered by exactly one tile cell
