        for tile in tiles[i]:
            if tile == j:
                count += 1
        return count == 1

    for i in range(n):
        for j in range(n):
            if i == j:
                continue
            if is_valid_square(tiles, i, j):
                if is_valid_tile(tiles, i, j):
                    return "YES"

    return "NO"

