            elif token[0] == "-":
                program.append(("-", 1))
            elif token[0] == ".":
                program.append(("print", 1))
            elif token[0] == ",":
                program.append(("getchar", 1))
            elif token[0] == "(":
                program.append(("(", 1))
            elif token[0] == ")":
                program.append((")", 1))
            elif token[0] == "[":
                program.append(("[", 1))
            elif token[0] == "]":
                program.append(("]", 1))
            elif token[0] == ";":
                program.append((";", 1))
            else:
                raise ValueError("unknown token")
        return program

    def execute_program(program):
        stack = []
        for instruction in program:
            op, num = instruction
            if op == ">=":
                stack[-1] = stack[-1] + num
            elif op == "<=":
                stack[-1] = stack[-1] - num
            elif op == "+":
                stack.append(stack[-1] + num)
            elif op == "-":
                stack.append(stack[-1] - num)
            elif op == "print":
                print(stack.pop())
            elif op == "getchar":
                print(chr(stack.pop()), end="")
            elif op == "(":
                stack.append(stack.pop())
            elif op == ")":
                while stack[-1]!= "(":
                    stack.append(stack.pop())
                stack.pop()
            elif op == "[":
                while stack[-1]!= "[":
                    stack.append(stack.pop())
                stack.append(stack.pop())
            elif op == "]":
                while stack[-1]!= "]":
                    stack.append(stack.pop())
                stack.pop()
            elif op == ";":
                pass
            else:
                raise ValueError("unknown operation")

    while True:
        program = parse_program(tokenize(next(read_input())))
        execute_program(program)

