    def in_list(point, points):
        for p in points:
            if p[0] == point[0] and p[1] == point[1]:
                return True
        return False

    # Define a function to check if a point is a key point
    def is_key_point(point, points):
        for i in range(1, len(points)):
            if not (points[i][0] == point[0] and points[i][1] == point[1]):
                return False
        return True

    # Define a function to calculate the minimum distance from the key point to the starting point
    def min_distance(starting_point, points):
        min_distance = float('inf')
        for point in points:
            distance = abs(starting_point[0] - point[0]) + abs(starting_point[1] - point[1])
            if distance < min_distance:
                min_distance = distance
        return min_distance

    # Find the key points with the minimum distance
    key_points = [(point[0], point[1]) for point in points if is_key_point(point, points)]
    key_point_distances = [(min_distance(key_points[0], points), key_points[0])]

    # Find the minimum possible total distance from the key point to the starting point
    for i in range(1, len(key_points)):
        current_distance = key_points[i][0] - key_point_distances[i-1][0]
        current_key_point = key_points[i]
        current_key_point_distances = key_point_distances[i]

        for j in range(len(points)):
            new_distance = current_distance + abs(points[j][0] - key_points[i][0]) + abs(points[j][1] - key_points[i][1])
            if new_distance < current_key_point_distances[j]:
                current_key_point = points[j]
                current_key_point_distances[j] = new_distance

        key_points.append(current_key_point)
        key_point_distances.append((current_distance, current_key_point))

    # Find the minimum possible total distance from the starting point to the key points
    total_distance = min_distance(key_points[0], points)
    return str(total_distance)

