    n, k = map(int, stdin.split())
    binary_string = list(map(int, stdin.split()))

    # swap two adjacent characters
    for i in range(len(binary_string) - 1):
        binary_string[i], binary_string[i+1] = binary_string[i+1], binary_string[i]

    # remove duplicate pairs of adjacent characters
    i = 0
    while i + 1 < len(binary_string):
        if binary_string[i] == binary_string[i+1]:
            binary_string[i], binary_string[i+1] = binary_string[i+1], binary_string[i]
            i += 1
        else:
            i += 1

    # remove duplicate pairs of adjacent characters
    i = 0
    while i + 1 < len(binary_string):
        if binary_string[i] == binary_string[i+1]:
            binary_string[i], binary_string[i+1] = binary_string[i+1], binary_string[i]
            i += 1
        else:
            i += 1

    # reverse the order of adjacent characters
    for i in range(len(binary_string) // 2):
        binary_string[i], binary_string[len(binary_string)-1-i] = binary_string[len(binary_string)-1-i], binary_string[i]

    # sort the string using the merge sort algorithm
    def merge(left, right):
        result = []
        i = j = 0
        while i < len(left) and j < len(right):
            if left[i] <= right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1
        result += left[i:]
        result += right[j:]
        return result

    def merge_sort(arr):
        if len(arr) <= 1:
            return arr
        mid = len(arr) // 2
        left = arr[:mid]
        right = arr[mid:]
        left = merge_sort(left)
        right = merge_sort(right)
        return merge(left, right)

    sorted_binary_string = merge_sort(binary_string)

    # perform k moves
    for _ in range(k):
        i, j = 0, len(sorted_binary_string) - 1
        while i < j:
            swap = sorted_binary_string[i]
            sorted_binary_string[i] = sorted_binary_string[j]
            sorted_binary_string[j] = swap
            i += 1
            j -= 1

    return ''.join(map(str, sorted_binary_string))

