def solution(stdin: str) -> str:
    t = int(stdin.strip())
    for _ in range(t):
        n = int(stdin.strip())
        s = stdin.strip()
        if sum(n) > 2000:
            continue
        if not s:
            return "No solution"

        def is_valid_substring(substring):
            if not substring:
                return True
            left, right = 0, len(substring) - 1
            while left < right:
                if substring[left]!= substring[right]:
                    return False
                left += 1
                right -= 1
            return True

        def remove_non_empty_substring(substring):
            left, right = 0, len(substring) - 1
            while left < right and substring[left] == 'L':
                left += 1
            while left < right and substring[right] == 'R':
                right -= 1
            return substring[:left+1] + substring[right+1:]

        def is_valid_endpoint(x, y):
            if not s[x:x+n]:
                return False
            if not s[x+n-1:x+n]:
                return False
            return s[x+n-1:x+n] == 'D'

        def optimize_path(start, end):
            start_x, start_y = start
            end_x, end_y = end
            path = []
            path.append((start_x, start_y))
            while start_x!= end_x or start_y!= end_y:
                if not s[start_x:start_x+n]:
                    start_x += 1
                else:
                    start_x += 1
                    path.append((start_x, start_y))
                if not s[end_x:end_x+n]:
                    end_x -= 1
                else:
                    end_x -= 1
                    path.append((end_x, end_y))
                if start_x == end_x and start_y == end_y:
                    break
                start_x, start_y = path[-1][0], path[-1][1]
            return path

        def optimize_robot_path(start, end):
            path = optimize_path(start, end)
            return path[n-1]

        optimized_paths = []
        for substring in s:
            if is_valid_substring(substring):
                optimized_paths.append(substring)

        for path in optimized_paths:
            optimized_path = optimize_robot_path(path, path + n - 1)
            if optimized_path[0]!= start[0] or optimized_path[1]!= start[1]:
                continue
            if not optimized_path:
                optimized_paths.remove(path)
            else:
                break

        if not optimized_paths:
            return "-1"
        else:
            l, r = 0, 0
            for i in range(len(optimized_paths)):
                if optimized_paths[i][l:r+1] == optimized_paths[i][l:r+1+n]:
                    l += n
            return f"{l} {r}"

