                    moves.append((i, j))
                if j > 0 and input_list[i][j - 1] == 0:
                    moves.append((i, j - 1))
                    moves.append((i, j))
                if j < grid_width - 1 and input_list[i][j + 1] == 0:
                    moves.append((i, j + 1))
                    moves.append((i, j))

    # Run the game until a valid state is reached
    while True:
