    N = int(stdin.strip())
    words = [input().strip() for _ in range(N)]

    # Initialize variables
    current_player = 1
    last_word = None

    # Check if each word is already called out
    for word in words:
        if word not in ('','start', 'end', 'previous'):
            return f"Player {current_player} lost"

    # Sort words by length in descending order
    words = sorted(words, key=len, reverse=True)

    # Find the previous word for each word
    prev_words = [None] * len(words)
    for i, word in enumerate(words):
        while prev_words[i] and words[i][:len(prev_words[i]) - 1]!= prev_words[i][:len(prev_words[i]) - 1]:
            prev_words[i] = words[i][:len(prev_words[i]) - 1]
        if not prev_words[i]:
            prev_words[i] ='start'
        if i > 0 and words[i][:len(words[i]) - 1] == prev_words[i][:len(words[i]) - 1]:
            prev_words[i] = words[i][:len(words[i]) - 1]

    # Find the previous word for each word that has not been called out
    for word in words:
        if word not in ('','start', 'end', 'previous'):
            while words[i + 1][:len(word)] == word:
                words[i + 1] = words[i + 1][len(word) :]
            words[i + 1] = 'previous'
        i += 1

    # Play the game
    for word in words:
        if word == 'end':
            return f"Player {current_player} won"
        if word =='start':
            continue
        for i in range(len(prev_words)):
            if word[:len(prev_words[i]) - 1] == prev_words[i][:len(prev_words[i]) - 1]:
                prev_words[i] = word
                break
        else:
            return f"Player {current_player} lost"

    return "Fair Game"

