            return value
        mid = (start + end) // 2
        left_max = get_max_value(start, mid, value)
        right_max = get_max_value(mid + 1, end, value)
        return max(left_max, right_max)

    for start in range(l1, r1 + 1):
        for end in range(l2, r2 + 1):
            max_value = get_max_value(start, end, float('inf'))
            for a, b in queries:
                if a <= start <= b and a <= end <= b:
                    if max_value == float('inf'):
                        break
                    if max_value == b:
                        max_value = b
                        break
                elif a <= end <= b and a <= start <= b:
                    if max_value == float('inf'):
                        break
                    if max_value == a:
                        max_value = a
                        break
            else:
                print(max_value)

    return ""

