def solution(stdin: str) -> str:
    lines = [line.strip() for line in stdin.split("\n")]
    P, D = map(int, lines[0].split())
    precincts = [[] for _ in range(P)]
    for line in lines[1:]:
        district, a, b = map(int, line.split())
        precincts[district - 1].append((a, b))

    def group_candidates(votes):
        candidates = {}
        for a, b in votes:
            if a not in candidates:
                candidates[a] = [b]
            else:
                candidates[a].append(b)
        return candidates

    def is_comparable(candidates):
        total_votes = sum(candidates.values())
        num_comparable = 0
        for i, num in enumerate(candidates.values()):
            if i + 1 == len(candidates) or num == candidates[i + 1]:
                num_comparable += 1
        return num_comparable > total_votes // 2

    def wasted_votes(precincts):
        total_votes = sum(sum(candidates.values()) for candidates in precincts)
        lost_votes = sum(1 for votes in precincts for a, b in votes if a!= b)
        excess_votes = total_votes - lost_votes
        wasted_votes = lost_votes - excess_votes
        return wasted_votes

    parties = {}
    for precincts in precincts:
        a_votes = [a for a, b in precincts]
        a_candidates = group_candidates(a_votes)
        if is_comparable(a_candidates):
            parties["A"] = "A"
        else:
            parties["A"] = "B"

        b_votes = [b for a, b in precincts]
        b_candidates = group_candidates(b_votes)
        if is_comparable(b_candidates):
            parties["B"] = "A"
        else:
            parties["B"] = "B"

    efficiency_gap = wasted_votes(precincts) / (P * D)
    for district in range(1, D + 1):
        party = parties.get(district, "Error: invalid district")
        print(f"{party} {wasted_votes(precincts[district - 1])} {wasted_votes(precincts[district - 1])}")

    return str(efficiency_gap)

