    N = int(stdin.readline().strip())
    players = list(map(int, stdin.readline().strip().split()))

    # Calculate the number of answers for each player
    num_answers = [0] * N
    for i in range(N):
        for j in range(i+1, N):
            num_answers[i] += 1
            num_answers[j] -= 1

    # Calculate the number of questions for each player
    num_questions = [0] * N
    for i in range(N):
        num_questions[players.index(i)] += 1

    # Calculate the number of seconds remaining for each player
    total_seconds = 0
    for i in range(N):
        if num_answers[i] > 0:
            total_seconds += num_answers[i]

    # Calculate the number of seconds for each player
    remaining_seconds = total_seconds

    # Calculate the number of questions for each player until the box explodes
    num_questions_until_box_explode = [0] * N
    for i in range(N):
        if num_questions[i] > 0:
            num_questions_until_box_explode[i] = num_questions[i]
            remaining_seconds -= num_answers[i]
            num_questions[i] = 0

    # Find the number of players who have the box exploded
    num_box_explodes = 0
    for i in range(N):
        if num_questions_until_box_explode[i] > 0:
            num_box_explodes += 1
            break

    # Find the number of players who have the box in the beginning
    num_box_starts = 0
    for i in range(N):
        if num_answers[i] > 0 and num_questions[i] > 0:
            num_box_starts += 1
            break

    # Find the number of players who have the box in the beginning and have the box exploded
    num_box_starts_and_box_explodes = 0
    for i in range(N):
        if num_answers[i] > 0 and num_questions[i] > 0:
            num_box_starts_and_box_explodes += 1
            break

    # Find the number of players who have the box in the beginning and have the box exploded and have the box started
    num_box_starts_and_box_explodes_and_box_starts = 0
    for i in range(N):
        if num_answers[i] > 0 and num_questions[i] > 0:
            num_box_starts_and_box_explodes_and_box_starts += 1
            break

    # Determine the numbered label of the player who had the box when it finally exploded
    if num_box_starts_and_box_explodes > 0:
        return str(players[num_box_starts_and_box_explodes - 1])
    elif num_box_starts > 0:
        return str(players[num_box_starts - 1])
