    t, N, M = map(int, stdin.split())
    wall_grid = input()

    if 'S' not in wall_grid:
        return "NOT POSSIBLE"

    # initialize visited list with all tiles as False
    visited = [[False for _ in range(M)] for _ in range(N)]

    # mark the starting tile as visited
    visited[0][0] = True

    # initialize variables to keep track of the minimum number of zones
    min_zones = 0
    min_zones_visited = 0

    # iterate until all tiles have been visited
    while all(not visited[i][j] for i in range(N) for j in range(M)):
        # find the fastest escape path
        min_zones_visited = 0
        min_zones = float('inf')

        for i in range(N):
            for j in range(M):
                if visited[i][j]:
                    # if the current tile has been visited, update the minimum number of zones
                    min_zones_visited += 1

                    # update the minimum number of zones if it is faster
                    if min_zones_visited < min_zones:
                        min_zones = min_zones_visited

        # mark the starting tile as not visited
        visited[0][0] = False

        # iterate until all tiles have been visited
        for i in range(N):
            for j in range(M):
                if visited[i][j]:
                    # if the current tile has not been visited, continue to the next tile
                    continue

                # if the current tile is not on the border, continue to the next tile
                if i == 0 or i == N-1 or j == 0 or j == M-1:
                    continue

                # if the current tile is on the left or right border, continue to the next tile
                if j > 0 and (wall_grid[i][j-1] == 'L' or wall_grid[i][j] == 'R'):
                    continue

                # if the current tile is above or below the border, continue to the next tile
                if i > 0 and (wall_grid[i-1][j] == 'U' or wall_grid[i][j] == 'D'):
                    continue

                # if the current tile is in the entrance zone, continue to the next tile
                if wall_grid[i][j] == 'U':
                    continue
                if wall_grid[i][j] == 'D':
                    continue

                # if the current tile is on the entrance zone, mark it as visited and move to the next tile
                visited[i][j] = True
                i += 1
                j -= 1
                min_zones_visited += 1

    # if all tiles have been visited, return the minimum number of zones
    if all(not visited[i][j] for i in range(N) for j in range(M)):
        return str(min_zones)
    else:
        return "NOT POSSIBLE"

