def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    sequence = list(map(int, stdin.split()[1:]))
    sequence.insert(0, sequence[0])

    degree = len(sequence)
    min_degree = 0

    for i in range(1, degree):
        if sequence[i] - sequence[i - 1] == 0:
            min_degree = i

    if min_degree == 0:
        return str(degree) + " " + str(sequence[0])

    polynomial_coefficients = [sequence[i] - sequence[i - 1] for i in range(min_degree + 1)]

    def polynomial(x):
        result = 1
        for coefficient in polynomial_coefficients:
            result *= x
            result -= coefficient
        return result

    f = lambda x: polynomial(x)
    df = lambda x: (polynomial(x + 1) - polynomial(x)) / (x + 1)

    differences = [df(x) for x in sequence[1:]]
    differences_table = [[f(x) for x in differences[:i + 1]] for i in range(len(differences))]

    def predict_value(x):
        value = 0
        for i, difference in enumerate(differences):
            value += differences_table[i][x] * difference
        return value

    output = str(min_degree) + " " + str(predict_value(sequence[n + 1]))

    return output

