def solution(stdin: str) -> str:
    sticks = [int(x) for x in stdin.split()]

    # Check if there are at least three sticks
    if len(sticks) < 3:
        return "possible"

    # Check if there are more than one stick with the same length
    if len(set(sticks))!= len(sticks):
        return "possible"

    # Check if the sum of the lengths of the sticks is odd
    if sum(sticks) % 2 == 1:
        return "possible"

    # Check if the sum of the lengths is equal to the sum of the sticks
    if sum(sticks)!= sum(set(sticks)):
        return "possible"

    # Check if the sum of the lengths of the sticks is greater than the sum of the sticks
    if sum(sticks) > sum(set(sticks)):
        return "possible"

    # If all checks pass, return impossible
    return "impossible"

