    runners = []
    for _ in range(n):
        name, time1, time2 = map(int, stdin.split())
        runners.append((name, time1, time2))

    def min_time(runners):
        min_time = 1000000000000000000000
        for runner in runners:
            time = abs(runner[1] - runner[0])
            if time < min_time:
                min_time = time
        return min_time

    min_time_runners = sorted(runners, key=lambda x: x[0])
    best_time = min_time_runners[0][1]

    def is_fastest(runner):
        return abs(runner[1] - runner[0]) < best_time

    def is_fastest_league(runners):
        fastest_runners = [runner for runner in runners if is_fastest(runner)]
        return len(fastest_runners) == 4

    fastest_league = []
    for runner in runners:
        if is_fastest(runner) and is_fastest_league(runners):
            fastest_league.append(runner)

    fastest_team = sorted(fastest_league, key=lambda x: x[0])

    result = '\n'.join(map(str, best_time)) + '\n'
    for runner in fastest_team:
        result += runner[0] + '\n'

    return result

